/* eslint-disable no-use-before-define */
import React from "react";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";

export default function Grouped() {
  const options = major.map((option) => {
    const firstLetter = option.major[0].toUpperCase();
    return {
      firstLetter: /[0-9]/.test(firstLetter) ? "0-9" : firstLetter,
      ...option,
    };
  });

  return (
    <Autocomplete
      id="grouped-demo"
      options={options.sort(
        (ก, ข) => -ก.firstLetter.localeCompare(ก.firstLetter)
      )}
      groupBy={(option) => option.firstLetter}
      getOptionLabel={(option) => option.major}
      fullWidth
      renderInput={(params) => (
        <TextField
          {...params}
          label="สาขาวิชาที่สำเร็จการศึกษา/กำลังศึกษาอยู่"
          variant="outlined"
        />
      )}
    />
  );
}

// Top 100 films as rated by IMDb users. http://www.imdb.com/chart/top
const major = [{ id_major: 1, major: "ปรัชญา ศาสนา เทววิทยา" }];
