import React from "react";
import styled from "styled-components";

import Cell from "./Cell";

export default function Row(props) {
  const { name, activity, context, mean, rate } = props;

  const getColor = (key) => {
    switch (key) {
      case "1":
        return "green";
      case "2":
        return "pink";
      default:
        return "white";
    }
  };

  return (
    <SRow>
      <Cell
        text={name}
        style={{
          background: "white",
          textAlign: "start",
          alignitems: "self-start",
          width: 200,
        }}
      />

      <Cell
        text={activity}
        style={{
          background: getColor(rate),
        }}
      />
      <Cell
        text={context}
        style={{
          background: getColor(rate),
        }}
      />
      <Cell
        text={mean}
        style={{
          background: "white",
        }}
      />
    </SRow>
  );
}

const SRow = styled.div`
  display: -webkit-flex;
  width: fit-content;
`;
