import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import SelectEducation from "../components/SelectEducation";
import SelectMajor from "../components/SelectMajor";

// สร้างstyle
const useStyles = makeStyles((theme) => ({
  formControl: {
    minWidth: 320,
  },
}));

export default function SimpleSelect({
  formProps: { register, errors },
  data,
}) {
  const classes = useStyles();
  const { education, majorName } = data[0];

  return (
    <Fragment>
      <h1>ประวัติการศึกษา</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.formControl}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                padding="2"
              >
                <SelectEducation
                  inputRef={register}
                  error={!!errors.education}
                  defaultValue={education}
                />
              </Grid>
            </Grid>
            <Grid item md={12} xs={12}>
              <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                padding="2"
              >
                <SelectMajor
                  inputRef={register}
                  error={!!errors.majorName}
                  defaultValue={majorName}
                />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
