import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import SelectEducation from "../components/SelectEducation";
import SelectMajor from "../components/SelectMajor";

// สร้างstyle

const useStyles = makeStyles((theme) => ({
  root: {
    "& inputProps:valid + fieldset": {
      borderWidth: 2,
    },
    "& inputProps:invalid + fieldset": {
      borderColor: "#f44336",
      borderWidth: 2,
    },
    "& inputProps:valid:focus + fieldset": {
      borderLeftWidth: 6,
      padding: "4px !important", // override inline-style
    },
  },
  formControl: {
    minWidth: 320,
  },
}));

export default function SimpleSelect({
  formProps: { register, errors },
  data,
}) {
  const classes = useStyles();
  //const { education, majorName } = data;

  return (
    <Fragment>
      <h1>ประวัติการศึกษา</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.formControl}>
          <div className={classes.root}>
            <Grid
              container
              direction="row"
              justify="center"
              alignItems="center"
              spacing={1}
            >
              <Grid item md={12} xs={12}>
                <Grid
                  container
                  direction="row"
                  justify="center"
                  alignItems="center"
                  padding="2"
                >
                  <SelectEducation
                    inputRef={register}
                    error={!!errors.education}
                    //defaultValue={education}
                  />
                </Grid>
              </Grid>
              <Grid item md={12} xs={12}>
                <Grid
                  container
                  direction="row"
                  justify="center"
                  alignItems="center"
                  padding="2"
                >
                  <SelectMajor
                    inputRef={register}
                    error={!!errors.majorName}
                    //  defaultValue={majorName}
                  />
                </Grid>
              </Grid>
            </Grid>
          </div>
        </div>
      </Grid>
    </Fragment>
  );
}
