import React from "react";
import styled from "styled-components";

import Cell from "./Cell";

export default function Row(props) {
  const { name, activity, context, mean, rate } = props;

  const getColor = (key) => {
    switch (key) {
      case "1":
        return "green";
      case "2":
        return "pink";
      default:
        return "white";
    }
  };

  return (
    <SRow>
      <Cell text={name} />

      <Cell
        text={activity}
        style={{
          width: 200,
          alignItems: "flex-start",
          justifyContent: "left",
          paddingLeft: 10,
          background: getColor(rate),
        }}
      />
      <Cell
        text={context}
        style={{
          width: 200,
          alignItems: "flex-start",
          justifyContent: "left",
          paddingLeft: 10,
          background: getColor(rate),
        }}
      />
      <Cell text={mean} />
    </SRow>
  );
}

const SRow = styled.div`
  display: flex;
  width: fit-content;
`;
