import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

// สร้างstyle
const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 320,
  },
}));

export default function SimpleSelect() {
  const classes = useStyles();
  const [major, setMajor] = React.useState("");
  const handleChange = (event) => {
    console.log(event.target.value);
    setMajor(event.target.value);
  };
  return (
    <FormControl className={classes.formControl}>
      <InputLabel id="select">
        สาขาวิชาที่สำเร็จการศึกษา/กำลังศึกษาอยู่
      </InputLabel>
      <Select
        labelId="select"
        id="select"
        value={major}
        onChange={handleChange}
      >
        <MenuItem value={"อักษรศาสตร์"}>อักษรศาสตร์</MenuItem>
        <MenuItem value={"ครุศาสตร์"}>ครุศาสตร์</MenuItem>
        <MenuItem value={"ประวัติศาสตร์"}>ประวัติศาสตร์</MenuItem>
        <MenuItem value={"ภาษาศาสตร์"}>ภาษาศาสตร์</MenuItem>
      </Select>
    </FormControl>
  );
}
