import React, { useRef, Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import SeletGender from "../components/SelectGender";
import SelectTitle from "../components/SelectTitle";
import { useFormContext } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
  root: {
    margin: "-1rem 0 2rem 0",
    padding: "0 7rem",
    [theme.breakpoints.down("xs")]: {
      padding: "0",
    },
    [theme.breakpoints.down("md")]: {
      padding: "0",
    },
    marginTop: "auto",
  },
  textField: {
    width: "100%",
    [theme.breakpoints.down("xs")]: {
      width: "100%",
    },
  },
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
  errorMessage: {
    color: "red",
    fontSize: "0.9rem",
    marginTop: "0.2rem",
  },
}));

export default function Information({ formProps: { register, errors }, data }) {
  const classes = useStyles();
  const methods = useFormContext();
  const onSumbit = (data) => console.log(data);
  //const { firstName, lastName, age, idCard } = data;
  // const firstRef = useRef("");
  //const lastRef = useRef("");
  //const idRef = useRef("");
  //const ageRef = useRef("");
  //const sendValue = () => {
  // console.log(firstRef.current.value);
  // console.log(lastRef.current.value);
  //console.log(ageRef.current.value);
  // console.log(idRef.current.value);
  // };

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.root}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid
              container
              direction="row"
              justify="left"
              alignItems="left"
              spacing={1}
            >
              <SelectTitle />
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                id="firstName"
                name="firstName"
                label="ชื่อ"
                margin="normal"
                variant="outlined"
                //required
                error={!!errors.firstName}
                className={classes.textField}
                //  defaultValue={firstName}
                //  inputRef={firstRef}
                // onSelect={sendValue}
                fullWidth
                ref={methods.register}
              />
              {errors.firstName && (
                <p className={classes.errorMessage}>
                  {errors.firstName.message}
                </p>
              )}
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                fullWidth
                id="lastName"
                label="นามสกุล"
                margin="normal"
                variant="outlined"
                //inputRef={lastRef}
                className={classes.textField}
                //onChange={sendValue}
                required
                error={!!errors.lastName}
                //defaultValue={lastName}
                ref={methods.register}
              />
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <SeletGender />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="age"
                  label="อายุ(ปี)"
                  margin="normal"
                  variant="outlined"
                  //  inputRef={ageRef}
                  //onSelect={sendValue}
                  className={classes.textField}
                  required
                  error={!!errors.age}
                  // defaultValue={age}
                  ref={methods.register}
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="idCard"
                  label="เลขประจำตัวประชาชน 13 หลัก"
                  margin="normal"
                  variant="outlined"
                  //  inputRef={idRef}
                  //onSelect={sendValue}
                  //required
                  className={classes.textField}
                  error={!!errors.idCard}
                  //defaultValue={idCard}
                  ref={methods.register}
                />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
