import React, { useRef, Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import SeletGender from "../components/SelectGender";
import SelectTitle from "../components/SelectTitle";
import { useFormContext } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
  root: {
    "& input:invalid + fieldset": {
      borderColor: "#f44336",
      borderWidth: 2,
    },
    "& input:valid:focus + fieldset": {
      borderColor: "#3f51b5",
      borderLeftWidth: 6,
      padding: "4px !important", // override inline-style
    },
  },
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
  errorMessage: {
    color: "red",
    fontSize: "0.9rem",
    marginTop: "0.2rem",
  },
}));

export default function Information({ formProps: { register, errors }, data }) {
  const classes = useStyles();
  const methods = useFormContext();
  //const onSumbit = (data) => console.log(data);
  //const { firstName, lastName, age, idCard } = data;
  // const firstRef = useRef("");
  //const lastRef = useRef("");
  const idRef = useRef("");
  //const ageRef = useRef("");
  //const sendValue = () => {
  // console.log(firstRef.current.value);
  // console.log(lastRef.current.value);
  //console.log(ageRef.current.value);
  // console.log(idRef.current.value);
  // };
  function checkID(id) {
    var i, sum;
    for (i = 0, sum = 0; i < 12; i++)
      sum += parseFloat(id.charAt(i)) * (13 - i);
    if ((11 - (sum % 11)) % 10 !== parseFloat(id.charAt(12))) return false;
    return true;
  }
  function checkForm() {
    if (!checkID(idRef.value)) alert("รหัสประชาชนไม่ถูกต้อง");
    else alert("รหัสประชาชนถูกต้อง");
  }

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.root}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid
              container
              direction="row"
              justify="left"
              alignItems="left"
              spacing={1}
            >
              <SelectTitle />
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                id="firstName"
                name="firstName"
                label="ชื่อ"
                margin="normal"
                variant="outlined"
                required
                error={!!errors.firstName}
                //  defaultValue={firstName}
                //  inputRef={firstRef}
                // onSelect={sendValue}
                fullWidth
                ref={methods.register}
              />
              {errors.firstName && (
                <p className={classes.errorMessage}>
                  {errors.firstName.message}
                </p>
              )}
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                fullWidth
                id="lastName"
                name="lastName"
                label="นามสกุล"
                margin="normal"
                variant="outlined"
                //inputRef={lastRef}
                //onChange={sendValue}
                required
                error={!!errors.lastName}
                //defaultValue={lastName}
                ref={methods.register}
              />
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <SeletGender />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="age"
                  name="age"
                  label="อายุ(ปี)"
                  margin="normal"
                  variant="outlined"
                  //  inputRef={ageRef}
                  //onSelect={sendValue}
                  required
                  error={!!errors.age}
                  // defaultValue={age}
                  ref={methods.register}
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <form name="form1" onsubmit={checkForm(idRef)}>
                  <TextField
                    fullWidth
                    id="idCard"
                    name="idCard"
                    label="เลขประจำตัวประชาชน 13 หลัก"
                    margin="normal"
                    variant="outlined"
                    inputRef={idRef}
                    //onSelect={sendValue}
                    required
                    error={!!errors.idCard}
                    //defaultValue={idCard}
                    ref={methods.register}
                  />
                  <input type="submit" value="ตรวจสอบ" />
                </form>
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
