import React from "react";
import styled from "styled-components";

import Cell from "./Cell";

export default function Row(props) {
  const { name, activity, context, mean, rate } = props;

  const getColor = (key) => {
    switch (key) {
      case "1":
        return "#ba000d";
      case "2":
        return "#ffff81";

      case "3":
        return "#ffffff";
      case "4":
        return "#3f51b5";
      case "5":
        return "#002984";
      default:
        return "white";
    }
  };

  const getTextColor = (key) => {
    switch (key) {
      case "1":
        return "#ffffff";
      case "2":
        return "#1b1b1b";

      case "3":
        return "#1b1b1b";
      case "4":
        return "#ffffff";
      case "5":
        return "#ffffff";
      default:
        return "black";
    }
  };

  return (
    <SRow>
      <Cell
        text={name}
        style={{
          background: "white",
          alginItem: "start",
          width: 200,
        }}
      />

      <Cell
        text={activity}
        style={{
          alginItem: "center",
          background: getColor(rate),
          color: getTextColor(rate),
        }}
      />
      <Cell
        text={context}
        style={{
          alginItem: "center",
          background: "white",
        }}
      />
      <Cell
        text={mean}
        style={{
          alginItem: "center",
          background: getColor(rate),
          color: getTextColor(rate),
        }}
      />
    </SRow>
  );
}

const SRow = styled.div`
  display: flex;
  width: fit-content;
`;
