import React, { useRef } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import SaveIcon from "@material-ui/icons/Save";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import SummaryActiveWorks from "./SummaryActiveWorks";
import { useReactToPrint } from "react-to-print";
import Table from "./TableData";
const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
}));

export default function ActivityWork() {
  const classes = useStyles();
  const componentRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  return (
    <Container maxWidth="sm" alignItems="center">
      <div ref={componentRef}>
        <Grid
          container
          direction="row"
          justify="center"
          alignItems="center"
          spacing={1}
        >
          <div className={classes.root}>
            <Grid
              container
              direction="row"
              justify="center"
              alignItems="center"
              spacing={1}
            >
              <Grid item md={12} xs={12}>
                <Grid>
                  <Container fixed alignItems="center">
                    <div alignItems="center">
                      <h1>สรุปผลประเมิน</h1>
                      <h3>ชื่อ สมใจ นามสกุล ยินดี</h3>
                      <h4>เลขบัตรประจำตัวประชาชน 1234567890123</h4>
                    </div>
                    <SummaryActiveWorks />
                  </Container>
                </Grid>
              </Grid>
            </Grid>
            <Grid container direction="row" spacing={1}>
              <Grid container spacing={4}>
                <Grid item xs={5}>
                  <Button
                    variant="contained"
                    color="secondary"
                    size="large"
                    className={classes.button}
                    startIcon={<SaveIcon />}
                    alignItems="left"
                    onClick={handlePrint}
                  >
                    Print
                  </Button>
                </Grid>
              </Grid>
            </Grid>
          </div>
        </Grid>
      </div>
    </Container>
  );
}
