import React from "react";
import Navbar from "../components/Navbar";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import { createMuiTheme } from "@material-ui/core/styles";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({}));
const StyledButton = withStyles({
  root: {
    background: "linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)",
    borderRadius: 3,
    border: 0,
    color: "white",
    height: 48,
    padding: "0 30px",
    boxShadow: "0 3px 5px 2px rgba(33, 203, 243, .3)",
  },
  label: {
    textTransform: "capitalize",
  },
})(Button);

export default function Home() {
  const classes = useStyles();
  const theme = createMuiTheme({
    typography: {
      fontFamily: '"Prompt", serif',
    },
  });

  return (
    <div className="container" theme={theme}>
      <div className="row">
        <Navbar />
        <Box position="relative">
          <center>
            <div className="box">
              Wellcome
              <div>
                สํานักงาน ก.พ. ร่วมกับ กรมการจัดหางาน กระทรวงแรงงาน
                พัฒนาแบบประเมินความสนใจของบุคคลที่ มีต่องานราชการไทย
                โดยมีวัตถุประสงค์เพื่อเตรียมความพร้อมให้แก่ผู้สนใจสมัครเข้ํารับราชการ
                ทุนรัฐบาล หรือประชาชนได้ทราบและนําข้อมูลเกี่ยวกับความถนัด
                ความสนใจ และบุคลิกภาพของตนเองไปใช้ ในการ
                ตัดสินใจเลือกประกอบอาชีพได้อย่างมีประสิทธิภาพ
                ตลอดจนเตรียมความพร้อมในการประกอบอาชีพ ก่อน เข้าสู่ตลาดแรงงาน
              </div>
              แบบประเมินฯ มีทั้งหมด 87 ข้อ ท่ํานต้องตอบแบบประเมินฯ ให้ครบทุกข้อ
              ใช้เวลําทั้งหมดประมําณ 15 นําที
              หํากท่ํานต้องกํารพิมพ์ผลกํารประเมินฯ
              โปรดเตรียมเครื่องพิมพ์ให้เรียบร้อยก่อนเริ่มทําแบบ ประเมินฯ
              หรือใชเ้ว็บเบรําวเซอร์ที่สํามํารถพิมพ์เป็นเอกสํารPDFได้
            </div>
            <StyledButton>
              <div> เริ่มทำแลลทดสอบ</div>
            </StyledButton>
          </center>
        </Box>
      </div>
    </div>
  );
}
