import React, { useRef, Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import SeletGender from "../components/SelectGender";
import SeletTilte from "../components/SelectTitle";
const useStyles = makeStyles((theme) => ({
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
}));

export default function Information(props) {
  const classes = useStyles();

  const firstRef = useRef("");
  const lastRef = useRef("");
  const idRef = useRef("");
  const ageRef = useRef("");
  const sendValue = () => {
    console.log(firstRef.current.value);
    console.log(lastRef.current.value);
    console.log(ageRef.current.value);
    console.log(idRef.current.value);
  };

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.root}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid
              container
              direction="row"
              justify="left"
              alignItems="left"
              spacing={1}
            >
              <SeletTilte fullWidth />
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                id="firstName"
                label="ชื่อ"
                margin="normal"
                variant="outlined"
                required
                inputRef={firstRef}
                onSelect={sendValue}
                fullWidth
              />
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                fullWidth
                id="lastName"
                label="นามสกุล"
                margin="normal"
                variant="outlined"
                inputRef={lastRef}
                onChange={sendValue}
                required
              />
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <SeletGender />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="id-card"
                  label="อายุ(ปี)"
                  margin="normal"
                  variant="outlined"
                  inputRef={ageRef}
                  onSelect={sendValue}
                  required
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="id-card"
                  label="เลขประจำตัวประชาชน 13 หลัก"
                  margin="normal"
                  variant="outlined"
                  inputRef={idRef}
                  onSelect={sendValue}
                  required
                />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
