import React, { useRef, Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import SelectMajor from "../components/SelectMajor";

// สร้างstyle
const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 140,
  },
}));

export default function SimpleSelect() {
  const classes = useStyles();
  const [edu, setEdu] = React.useState("");
  const majorRef = useRef("");
  const sendValue = () => {
    console.log(majorRef.current.value);
  };
  const handleChange = (event) => {
    console.log(event.target.value);
    setEdu(event.target.value);
  };
  return (
    <Fragment>
      <h1>Education</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.root}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              <Grid
                container
                direction="row"
                justify="flex-start"
                alignItems="flex-start"
                padding="2"
              >
                <FormControl className={classes.formControl}>
                  <InputLabel id="select">ระดับการศึกษา</InputLabel>
                  <Select
                    labelId="select"
                    id="select"
                    value={edu}
                    onChange={handleChange}
                  >
                    <MenuItem value={"ปวช"}>ปวช</MenuItem>
                    <MenuItem value={"ปริญญาตรี"}>ปริญญาตรี</MenuItem>
                    <MenuItem value={"ปริญญาโท"}>ปริญญาโท</MenuItem>
                    <MenuItem value={"ปริญญาเอก"}>ปริญญาเอก</MenuItem>
                    <MenuItem value={"อื่นๆ"}>อื่นๆ</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
            <Grid item md={12} xs={12}>
              <SelectMajor />
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
