/* eslint-disable no-use-before-define */
import React from "react";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";

export default function Grouped() {
  const options = major.map((option) => {
    const firstLetter = option.major[0].toUpperCase();
    return {
      firstLetter: /[0-9]/.test(firstLetter) ? "0-9" : firstLetter,
      ...option,
    };
  });

  return (
    <Autocomplete
      options={options.sort(
        (ก, อ) => -อ.firstLetter.localeCompare(ก.firstLetter)
      )}
      groupBy={(option) => option.firstLetter}
      getOptionLabel={(option) => option.major}
      fullWidth
      id="auto-complete"
      autoComplete
      includeInputInList
      renderInput={(params) => (
        <TextField {...params} label="ระดับการศึกษา" margin="normal" />
      )}
    />
  );
}

const major = [
  { id_major: 1, major: "ประถมศึกษา" },
  { id_major: 2, major: "มัธยมต้น" },
  { id_major: 3, major: "มัธยมปลาย" },
  { id_major: 4, major: "ปวช." },
  { id_major: 5, major: "ปวท." },
  { id_major: 6, major: "ปวส." },
  { id_major: 7, major: "ประกาศนียบัตร" },
  { id_major: 8, major: "ประกาศนียบัตรบัณฑิต" },
  { id_major: 9, major: "ประกาศนียบัตรบัณฑิตขั้นสูง" },
  { id_major: 10, major: "อนุปริญญา" },
  { id_major: 11, major: "ปริญญาตรี" },
  { id_major: 13, major: "ปริญญาโท" },
  { id_major: 13, major: "ปริญญาเอก" },
];
