/* eslint-disable no-use-before-define */
import React from "react";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";

export default function Grouped() {
  const [value, setValue] = React.useState(education[0]);
  const [inputValue, setInputValue] = React.useState("");

  const options = education.map((option) => {
    const firstLetter = option.major[0].toUpperCase();
    return {
      firstLetter: /[0-9]/.test(firstLetter) ? "0-9" : firstLetter,
      ...option,
    };
  });

  return (
    <Autocomplete
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      inputValue={inputValue}
      onInputChange={(event, newInputValue) => {
        setInputValue(newInputValue);
      }}
      options={options.sort(
        (ก, อ) => -อ.firstLetter.localeCompare(ก.firstLetter)
      )}
      groupBy={(option) => option.firstLetter}
      getOptionLabel={(option) => option.education}
      fullWidth
      id="auto-complete"
      autoComplete
      includeInputInList
      renderInput={(params) => (
        <TextField {...params} label="ระดับการศึกษา" margin="normal" />
      )}
    />
  );
}

const education = [
  { id_education: 1, education: "ประถมศึกษา" },
  { id_education: 2, education: "มัธยมต้น" },
  { id_education: 3, education: "มัธยมปลาย" },
  { id_education: 4, education: "ปวช." },
  { id_education: 5, education: "ปวท." },
  { id_education: 6, education: "ปวส." },
  { id_education: 7, education: "ประกาศนียบัตร" },
  { id_education: 8, education: "ประกาศนียบัตรบัณฑิต" },
  { id_education: 9, education: "ประกาศนียบัตรบัณฑิตขั้นสูง" },
  { id_education: 10, education: "อนุปริญญา" },
  { id_education: 11, education: "ปริญญาตรี" },
  { id_education: 13, education: "ปริญญาโท" },
  { id_education: 13, education: "ปริญญาเอก" },
];
